import React, { Component } from "react";

class App extends Component {
  state = {
    longitude: "",
    latitude: "",
    url: ""
  };
  componentWillMount() {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(position => {
        this.setState({
          longitude: position.coords.longitude.toString(),
          latitude: position.coords.latitude.toString(),
          url:
            "https://api.openweathermap.org/data/2.5/forecast?lat=" +
            this.state.latitude +
            "&lon=" +
            this.state.longitude +
            "&appid=d8862f1c1a5e2782aff94192f1248331"
        });
      });
    }
  }
  componentDidMount() {
    if (this.state.url) {
      fetch(this.state.url)
        .then(res => res.json())
        .then(data => console.log(data));
    }
  }
  render() {
    return (
      <div className="container con">
        <div className="row con2 justify-content-center">
          <div className="col-sm-2 bor ">
            <p>Wed</p>
            <i className="wi wi-day-snow size" />
            <p>
              <span className="lead">78</span>
              &#176; &nbsp;&nbsp;
              <span className="text-muted">67</span>
              &#176;
            </p>
          </div>
          <div className="col-sm-2 bor">
            <p>Wed</p>
            <i className="wi wi-day-cloudy size" />
            <p>
              <span className="lead">78</span>
              &#176; &nbsp;&nbsp;
              <span className="text-muted">67</span>
              &#176;
            </p>
          </div>
          <div className="col-sm-2 bor">
            <p>Wed</p>
            <i className="wi wi-day-rain size" />
            <p>
              <span className="lead">78</span>
              &#176; &nbsp;&nbsp;
              <span className="text-muted">67</span>
              &#176;
            </p>
          </div>
          <div className="col-sm-2 bor">
            <p>Wed</p>
            <i className="wi wi-day-sunny size" />
            <p>
              <span className="lead">78</span>
              &#176; &nbsp;&nbsp;
              <span className="text-muted">67</span>
              &#176;
            </p>
          </div>
          <div className="col-sm-2 bor">
            <p>Wed</p>
            <i className="wi wi-day-cloudy size" />
            <p>
              <span className="lead">78</span>
              &#176; &nbsp;&nbsp;
              <span className="text-muted">67</span>
              &#176;
            </p>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
