import React, { Component } from "react";

class App extends Component {
  state = {
    url: ""
  };
  constructor() {
    super();
    if (navigator.geolocation) {
      console.log("running");
      navigator.geolocation.getCurrentPosition(position => {
        console.log(position.coords.latitude);
        this.setState({
          url: `https://api.openweathermap.org/data/2.5/forecast?lat=${parseFloat(
            position.coords.latitude
          )}&lon=${parseFloat(
            position.coords.longitude
          )}&appid=d8862f1c1a5e2782aff94192f1248331`
        });
      });
    }
  }
  componentDidMount() {
    console.log("componentdidmount");
    if (navigator.geolocation) {
      console.log("running");
      navigator.geolocation.getCurrentPosition(position => {
        console.log(position.coords.latitude);
        this.setState({
          url: `https://api.openweathermap.org/data/2.5/forecast?lat=${parseFloat(
            position.coords.latitude
          )}&lon=${parseFloat(
            position.coords.longitude
          )}&appid=d8862f1c1a5e2782aff94192f1248331`
        });
      });
    }

    console.log(this.state.url);
    fetch(this.state.url)
      .then(res => res.json())
      .then(data => {
        console.log(data);
      });
  }
  render() {
    if (this.state.url) {
      return (
        <div className="container con">
          <div className="row con2 justify-content-center">
            <div className="col-sm-2 bor ">
              <p>Wed</p>
              <i className="wi wi-day-snow size" />
              <p>
                <span className="lead">78</span>
                &#176; &nbsp;&nbsp;
                <span className="text-muted">67</span>
                &#176;
              </p>
            </div>
            <div className="col-sm-2 bor">
              <p>Wed</p>
              <i className="wi wi-day-cloudy size" />
              <p>
                <span className="lead">78</span>
                &#176; &nbsp;&nbsp;
                <span className="text-muted">67</span>
                &#176;
              </p>
            </div>
            <div className="col-sm-2 bor">
              <p>Wed</p>
              <i className="wi wi-day-rain size" />
              <p>
                <span className="lead">78</span>
                &#176; &nbsp;&nbsp;
                <span className="text-muted">67</span>
                &#176;
              </p>
            </div>
            <div className="col-sm-2 bor">
              <p>Wed</p>
              <i className="wi wi-day-sunny size" />
              <p>
                <span className="lead">78</span>
                &#176; &nbsp;&nbsp;
                <span className="text-muted">67</span>
                &#176;
              </p>
            </div>
            <div className="col-sm-2 bor">
              <p>Wed</p>
              <i className="wi wi-day-cloudy size" />
              <p>
                <span className="lead">78</span>
                &#176; &nbsp;&nbsp;
                <span className="text-muted">67</span>
                &#176;
              </p>
            </div>
          </div>
        </div>
      );
    } else {
      return (
        <div className="container">
          <h2>Loading...</h2>
        </div>
      );
    }
  }
}

export default App;
