import React, { Component } from "react";
import Columns from "./colums";
class Main extends Component {
  state = {
    data: null,
    days: []
  };
  componentDidMount() {
    fetch(this.props.url)
      .then(res => res.json())
      .then(data => {
        this.setState({
          data: data.list,
          days: [
            data.list[0].dt,
            data.list[0].dt + 24 * 60 * 60,
            data.list[0].dt + 24 * 60 * 60 * 2,
            data.list[0].dt + 24 * 60 * 60 * 3,
            data.list[0].dt + 24 * 60 * 60 * 4,
            data.list[0].dt + 24 * 60 * 60 * 5
          ]
        });
      })
      .then(() => {
        const weatherDays = this.state.data.filter(
          list => this.state.days.includes(list.dt)
          // list.dt.includes(this.state.days)
        );
        console.log(weatherDays);
      });
  }
  render() {
    if (this.state.data) {
      console.log(this.state.data);
      console.log(this.state.days);
      console.log(weatherDays);
      return (
        <div className="row con2 justify-content-center">
          <Columns day1={this.state.data[0]} />
          <Columns day1={this.state.data[1]} />
          <Columns day1={this.state.data[2]} />
          <Columns day1={this.state.data[3]} />
          <Columns day1={this.state.data[4]} />
          <Columns day1={this.state.data[5]} />
        </div>
      );
    } else {
      return (
        <div className="loading">
          <h2>Loading...</h2>
        </div>
      );
    }
  }
}
export default Main;
